mapper.insert:
insert into customertransfer (customer_id, transuser_id, transtime,
oldseller_id, newseller_id,
transreason
)
values (#{customer.id,jdbcType=BIGINT}, #{transuser.id,jdbcType=BIGINT}, now(),
#{oldseller.id,jdbcType=BIGINT}, #{newseller.id,jdbcType=BIGINT}, #{transreason,jdbcType=VARCHAR}
)

mapper.select:
select
from customertransfer tran LEFT JOIN customer c on tran.customer_id = c.id LEFT JOIN employee opuser on tran.transUser_id = opuser.id LEFT JOIN employee old on tran.oldSeller_id = old.id LEFT JOIN employee new on tran.newSeller_id = new.id
limit #{start},#{rows}
